mapper.select:
SELECT * FROM ( SELECT row_number() over (ORDER BY system_login_date DESC )AS no,
id, user_id, email, system_login_date, system_logout_date, ip_address, device_type, os_name, browser_name FROM SYS_ACCS_LOG
AND id = #{id} AND user_id = #{user_id} AND email = #{email} AND system_login_date = #{system_login_date} AND system_logout_date = #{system_logout_date} AND ip_address = #{ip_address} AND device_type = #{device_type} AND os_name = #{os_name} AND browser_name = #{browser_name}
AND id LIKE CONCAT('%', #{_id}, '%') AND user_id LIKE CONCAT('%', #{_user_id}, '%') AND email LIKE CONCAT('%', #{_email}, '%') AND system_login_date LIKE CONCAT('%', #{_system_login_date}, '%') AND system_logout_date LIKE CONCAT('%', #{_system_logout_date}, '%') AND ip_address LIKE CONCAT('%', #{_ip_address}, '%') AND device_type LIKE CONCAT('%', #{_device_type}, '%') AND os_name LIKE CONCAT('%', #{_os_name}, '%') AND browser_name LIKE CONCAT('%', #{_browser_name}, '%')
AND system_login_date >= STR_TO_DATE(#{from_date}, '%Y%m%d') AND system_login_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)
AND system_login_date >= STR_TO_DATE(#{from_date}, '%Y%m%d') AND system_login_date < DATE_ADD(STR_TO_DATE(#{from_date}, '%Y%m%d'), INTERVAL 1 DAY)
AND system_login_date >= STR_TO_DATE(#{to_date}, '%Y%m%d') AND system_login_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)
) AS sub
LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_ACCS_LOG


    AND id = #{id}
    AND user_id = #{user_id}
    AND email = #{email}
    AND system_login_date = #{system_login_date}
    AND system_logout_date = #{system_logout_date}
    AND ip_address = #{ip_address}
    AND device_type = #{device_type}
    AND os_name = #{os_name}
    AND browser_name = #{browser_name}

    AND id LIKE CONCAT('%', #{_id}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND email LIKE CONCAT('%', #{_email}, '%')
    AND system_login_date LIKE CONCAT('%', #{_system_login_date}, '%')
    AND system_logout_date LIKE CONCAT('%', #{_system_logout_date}, '%')
    AND ip_address LIKE CONCAT('%', #{_ip_address}, '%')
    AND device_type LIKE CONCAT('%', #{_device_type}, '%')
    AND os_name LIKE CONCAT('%', #{_os_name}, '%')
    AND browser_name LIKE CONCAT('%', #{_browser_name}, '%')

        AND system_login_date >= STR_TO_DATE(#{from_date}, '%Y%m%d')
        AND system_login_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)



        AND system_login_date >= STR_TO_DATE(#{from_date}, '%Y%m%d')
        AND system_login_date < DATE_ADD(STR_TO_DATE(#{from_date}, '%Y%m%d'), INTERVAL 1 DAY)



        AND system_login_date >= STR_TO_DATE(#{to_date}, '%Y%m%d')
        AND system_login_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)

SELECT

    id,
    user_id,
    email,
    system_login_date,
    system_logout_date,
    ip_address,
    device_type,
    os_name,
    browser_name
FROM SYS_ACCS_LOG


    AND id = #{id}
    AND user_id = #{user_id}
    AND email = #{email}
    AND system_login_date = #{system_login_date}
    AND system_logout_date = #{system_logout_date}
    AND ip_address = #{ip_address}
    AND device_type = #{device_type}
    AND os_name = #{os_name}
    AND browser_name = #{browser_name}

    AND id LIKE CONCAT('%', #{_id}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND email LIKE CONCAT('%', #{_email}, '%')
    AND system_login_date LIKE CONCAT('%', #{_system_login_date}, '%')
    AND system_logout_date LIKE CONCAT('%', #{_system_logout_date}, '%')
    AND ip_address LIKE CONCAT('%', #{_ip_address}, '%')
    AND device_type LIKE CONCAT('%', #{_device_type}, '%')
    AND os_name LIKE CONCAT('%', #{_os_name}, '%')
    AND browser_name LIKE CONCAT('%', #{_browser_name}, '%')

