mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY sys_crt_dt DESC )AS no,

        file_id, 
        file_uuid, 
        file_nm, 
        file_sz, 
        file_ext, 
        file_path, 
        sys_crt_dt, 
        sys_crt_usr_id, 
        sys_upd_dt, 
        sys_upd_usr_id 
    FROM SYS_FILE_DTL


        AND file_id = #{file_id}
        AND file_uuid = #{file_uuid}
        AND file_nm = #{file_nm}
        AND file_sz = #{file_sz}
        AND file_ext = #{file_ext}
        AND file_path = #{file_path}
        AND sys_crt_dt = #{sys_crt_dt}
        AND sys_crt_usr_id = #{sys_crt_usr_id}
        AND sys_upd_dt = #{sys_upd_dt}
        AND sys_upd_usr_id = #{sys_upd_usr_id}

        AND file_id LIKE CONCAT('%', #{_file_id}, '%')
        AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
        AND file_nm LIKE CONCAT('%', #{_file_nm}, '%')
        AND file_sz LIKE CONCAT('%', #{_file_sz}, '%')
        AND file_ext LIKE CONCAT('%', #{_file_ext}, '%')
        AND file_path LIKE CONCAT('%', #{_file_path}, '%')
        AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
        AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
        AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
        AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_FILE_DTL


    AND file_id = #{file_id}
    AND file_uuid = #{file_uuid}
    AND file_nm = #{file_nm}
    AND file_sz = #{file_sz}
    AND file_ext = #{file_ext}
    AND file_path = #{file_path}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND file_id LIKE CONCAT('%', #{_file_id}, '%')
    AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
    AND file_nm LIKE CONCAT('%', #{_file_nm}, '%')
    AND file_sz LIKE CONCAT('%', #{_file_sz}, '%')
    AND file_ext LIKE CONCAT('%', #{_file_ext}, '%')
    AND file_path LIKE CONCAT('%', #{_file_path}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

SELECT

    file_id,
    file_uuid,
    file_nm,
    file_sz,
    file_ext,
    file_path,
    sys_crt_dt,
    sys_crt_usr_id,
    sys_upd_dt,
    sys_upd_usr_id
FROM SYS_FILE_DTL


    AND file_id = #{file_id}
    AND file_uuid = #{file_uuid}
    AND file_nm = #{file_nm}
    AND file_sz = #{file_sz}
    AND file_ext = #{file_ext}
    AND file_path = #{file_path}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND file_id LIKE CONCAT('%', #{_file_id}, '%')
    AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
    AND file_nm LIKE CONCAT('%', #{_file_nm}, '%')
    AND file_sz LIKE CONCAT('%', #{_file_sz}, '%')
    AND file_ext LIKE CONCAT('%', #{_file_ext}, '%')
    AND file_path LIKE CONCAT('%', #{_file_path}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

mapper.insert:
INSERT INTO SYS_FILE_DTL(

     file_id 
    , file_uuid 
    , file_nm 
    , file_sz 
    , file_ext 
    , file_path 
    , sys_crt_dt 
    , sys_crt_usr_id 

    , sys_upd_dt
    , sys_upd_usr_id
)
VALUES(

     #{file_id}
    , #{file_uuid}
    , #{file_nm}
    , #{file_sz}
    , #{file_ext}
    , #{file_path}
    , #{sys_crt_dt}
    , #{sys_crt_usr_id}

    , #{sys_upd_dt}
    , #{sys_upd_usr_id}
)

mapper.delete:
DELETE FROM SYS_FILE_DTL

    AND file_id = #{file_id}
    AND file_uuid = #{file_uuid}

