mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY system_create_date DESC )AS no,

        file_uuid,
        temp_yn,
        system_create_date, 
        system_create_userid, 
        system_update_date, 
        system_update_userid 
    FROM SYS_FILE


        AND file_uuid = #{file_uuid}
        AND temp_yn = #{temp_yn}
        AND system_create_date = #{system_create_date}
        AND system_create_userid = #{system_create_userid}
        AND system_update_date = #{system_update_date}
        AND system_update_userid = #{system_update_userid}

        AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
        AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
        AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
        AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
        AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_FILE


    AND file_uuid = #{file_uuid}
    AND temp_yn = #{temp_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}

    AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

SELECT

    file_uuid,
    temp_yn,
    system_create_date,
    system_create_userid,
    system_update_date,
    system_update_userid
FROM SYS_FILE


    AND file_uuid = #{file_uuid}
    AND temp_yn = #{temp_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
    AND system_update_date = #{system_update_date}
    AND system_update_userid = #{system_update_userid}

    AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')

mapper.insert:
INSERT INTO SYS_FILE(

     file_uuid
    , temp_yn
    , system_create_date 
    , system_create_userid 

    , system_update_date
    , system_update_userid
)
VALUES(

     #{file_uuid}
    , #{temp_yn}
    , #{system_create_date}
    , #{system_create_userid}

    , #{system_update_date}
    , #{system_update_userid}
)

INSERT INTO SYS_FILE_DETAIL(
file_id
,file_uuid
, file_name
, file_size
, file_extension
, file_path
, system_create_date
, system_create_userid

, system_update_date
, system_update_userid
, menu_code
)
VALUES(

  #{file_id}
, #{file_uuid}
, #{file_name}
, #{file_size}
, #{file_extension}
, #{file_path}
, #{system_create_date}
, #{system_create_userid}

, #{system_update_date}
, #{system_update_userid}
, #{menu_code}
)

mapper.delete:
DELETE FROM SYS_FILE


    AND file_uuid = #{file_uuid}

    AND temp_yn = #{temp_yn}
    AND system_create_date = #{system_create_date}
    AND system_create_userid = #{system_create_userid}
