mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY sys_crt_dt DESC )AS no,

        file_uuid,
        temp_yn,
        sys_crt_dt, 
        sys_crt_usr_id, 
        sys_upd_dt, 
        sys_upd_usr_id 
    FROM SYS_FILE


        AND file_uuid = #{file_uuid}
        AND temp_yn = #{temp_yn}
        AND sys_crt_dt = #{sys_crt_dt}
        AND sys_crt_usr_id = #{sys_crt_usr_id}
        AND sys_upd_dt = #{sys_upd_dt}
        AND sys_upd_usr_id = #{sys_upd_usr_id}

        AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
        AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
        AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
        AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
        AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_FILE


    AND file_uuid = #{file_uuid}
    AND temp_yn = #{temp_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

SELECT

    file_uuid,
    temp_yn,
    sys_crt_dt,
    sys_crt_usr_id,
    sys_upd_dt,
    sys_upd_usr_id
FROM SYS_FILE


    AND file_uuid = #{file_uuid}
    AND temp_yn = #{temp_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND file_uuid LIKE CONCAT('%', #{_file_uuid}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

mapper.insert:
INSERT INTO SYS_FILE(

     file_uuid
    , temp_yn
    , sys_crt_dt 
    , sys_crt_usr_id 

    , sys_upd_dt
    , sys_upd_usr_id
)
VALUES(

     #{file_uuid}
    , #{temp_yn}
    , #{sys_crt_dt}
    , #{sys_crt_usr_id}

    , #{sys_upd_dt}
    , #{sys_upd_usr_id}
)

INSERT INTO SYS_FILE_DTL(
file_id
,file_uuid
, file_nm
, file_sz
, file_ext
, file_path
, sys_crt_dt
, sys_crt_usr_id

, sys_upd_dt
, sys_upd_usr_id
, menu_cd
)
VALUES(

  #{file_id}
, #{file_uuid}
, #{file_nm}
, #{file_sz}
, #{file_ext}
, #{file_path}
, #{sys_crt_dt}
, #{sys_crt_usr_id}

, #{sys_upd_dt}
, #{sys_upd_usr_id}
, #{menu_cd}
)

mapper.delete:
DELETE FROM SYS_FILE


    AND file_uuid = #{file_uuid}

    AND temp_yn = #{temp_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}
