mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY sys_crt_dt DESC )AS no,

        menu_cd, 
        acs_rts_grp_id, 
        sys_crt_dt, 
        sys_crt_usr_id, 
        sys_upd_dt, 
        sys_upd_usr_id 
    FROM SYS_ACS_GRP_MENU


        AND menu_cd = #{menu_cd}
        AND acs_rts_grp_id = #{acs_rts_grp_id}
        AND sys_crt_dt = #{sys_crt_dt}
        AND sys_crt_usr_id = #{sys_crt_usr_id}
        AND sys_upd_dt = #{sys_upd_dt}
        AND sys_upd_usr_id = #{sys_upd_usr_id}

        AND menu_cd LIKE CONCAT('%', #{_menu_cd}, '%')
        AND acs_rts_grp_id LIKE CONCAT('%', #{_acs_rts_grp_id}, '%')
        AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
        AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
        AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
        AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_ACS_GRP_MENU


    AND menu_cd = #{menu_cd}
    AND acs_rts_grp_id = #{acs_rts_grp_id}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND menu_cd LIKE CONCAT('%', #{_menu_cd}, '%')
    AND acs_rts_grp_id LIKE CONCAT('%', #{_acs_rts_grp_id}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

SELECT

    menu_cd,
    acs_rts_grp_id,
    sys_crt_dt,
    sys_crt_usr_id,
    sys_upd_dt,
    sys_upd_usr_id
FROM SYS_ACS_GRP_MENU


    AND menu_cd = #{menu_cd}
    AND acs_rts_grp_id = #{acs_rts_grp_id}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND menu_cd LIKE CONCAT('%', #{_menu_cd}, '%')
    AND acs_rts_grp_id LIKE CONCAT('%', #{_acs_rts_grp_id}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

mapper.insert:
INSERT INTO SYS_ACS_GRP_MENU(

     menu_cd 
    , acs_rts_grp_id 

    , sys_crt_dt
    , sys_crt_usr_id
    , sys_upd_dt
    , sys_upd_usr_id
)
VALUES(

     #{menu_cd}
    , #{acs_rts_grp_id}

    , #{sys_crt_dt}
    , #{sys_crt_usr_id}
    , #{sys_upd_dt}
    , #{sys_upd_usr_id}
)

mapper.delete:
DELETE FROM SYS_ACS_GRP_MENU


     menu_cd = #{menu_cd}
    AND acs_rts_grp_id = #{acs_rts_grp_id}

    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}
