mapper.select:
SELECT * FROM ( SELECT row_number() over (ORDER BY system_create_date DESC )AS no,
bbs_mst_id, bbs_id, bbs_type, bbs_nm, bbs_manager, file_yn, reply_yn, system_create_userid, system_create_date, system_update_userid, system_update_date, p_menu_code, (SELECT COUNT(*) FROM SYS_ACCS_GROUP_MENU SAGM WHERE SAGM.MENU_CODE = SYS_BBS.BBS_ID) AS access_group_count FROM SYS_BBS
AND bbs_mst_id = #{bbs_mst_id} AND bbs_id = #{bbs_id} AND bbs_type = #{bbs_type} AND bbs_nm = #{bbs_nm} AND bbs_manager = #{bbs_manager} AND file_yn = #{file_yn} AND reply_yn = #{reply_yn} AND system_create_userid = #{system_create_userid} AND system_create_date = #{system_create_date} AND system_update_userid = #{system_update_userid} AND system_update_date = #{system_update_date}
AND bbs_mst_id LIKE CONCAT('%', #{_bbs_mst_id}, '%') AND bbs_id LIKE CONCAT('%', #{_bbs_id}, '%') AND bbs_nm LIKE CONCAT('%', #{_bbs_nm}, '%') AND bbs_manager LIKE CONCAT('%', #{_bbs_manager}, '%') AND file_yn LIKE CONCAT('%', #{_file_yn}, '%') AND reply_yn LIKE CONCAT('%', #{_reply_yn}, '%') AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%') AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%') AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%') AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')
AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d') AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)
AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d') AND system_create_date < DATE_ADD(STR_TO_DATE(#{from_date}, '%Y%m%d'), INTERVAL 1 DAY)
AND system_create_date >= STR_TO_DATE(#{to_date}, '%Y%m%d') AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)
) AS sub
LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_BBS


    AND bbs_mst_id = #{bbs_mst_id}
    AND bbs_id = #{bbs_id}
    AND bbs_nm = #{bbs_nm}
    AND bbs_manager = #{bbs_manager}
    AND file_yn = #{file_yn}
    AND reply_yn = #{reply_yn}
    AND system_create_userid = #{system_create_userid}
    AND system_create_date = #{system_create_date}
    AND system_update_userid = #{system_update_userid}
    AND system_update_date = #{system_update_date}

    AND bbs_mst_id LIKE CONCAT('%', #{_bbs_mst_id}, '%')
    AND bbs_id LIKE CONCAT('%', #{_bbs_id}, '%')
    AND bbs_nm LIKE CONCAT('%', #{_bbs_nm}, '%')
    AND bbs_manager LIKE CONCAT('%', #{_bbs_manager}, '%')
    AND file_yn LIKE CONCAT('%', #{_file_yn}, '%')
    AND reply_yn LIKE CONCAT('%', #{_reply_yn}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')

        AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d')
        AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)



        AND system_create_date >= STR_TO_DATE(#{from_date}, '%Y%m%d')
        AND system_create_date < DATE_ADD(STR_TO_DATE(#{from_date}, '%Y%m%d'), INTERVAL 1 DAY)



        AND system_create_date >= STR_TO_DATE(#{to_date}, '%Y%m%d')
        AND system_create_date < DATE_ADD(STR_TO_DATE(#{to_date}, '%Y%m%d'), INTERVAL 1 DAY)

SELECT

    bbs_mst_id,
    bbs_id,
    bbs_type,
    bbs_nm,
    bbs_manager,
    file_yn,
    reply_yn,
    system_create_userid,
