    (SELECT COUNT(*) FROM SYS_ACCS_GROUP_MENU SAGM WHERE SAGM.MENU_CODE = SYS_BBS.BBS_ID) AS access_group_count
FROM SYS_BBS


    AND bbs_mst_id = #{bbs_mst_id}
    AND bbs_id = #{bbs_id}
    AND bbs_type = #{bbs_type}
    AND bbs_nm = #{bbs_nm}
    AND bbs_manager = #{bbs_manager}
