mapper.select:
SELECT
    row_number() over (ORDER BY sys_crt_dt DESC )AS no,
    cd_id, 
    grp_id, 
    cd_nm, 
    cd_no, 
    use_yn, 
    sys_crt_dt, 
    sys_crt_usr_id, 
    sys_upd_dt, 
    sys_upd_usr_id 
FROM SYS_CD


    AND cd_id = #{cd_id}
    AND grp_id = #{grp_id}
    AND cd_nm = #{cd_nm}
    AND cd_no = #{cd_no}
    AND use_yn = #{use_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND cd_id LIKE CONCAT('%', #{_cd_id}, '%')
    AND grp_id LIKE CONCAT('%', #{_grp_id}, '%')
    AND cd_nm LIKE CONCAT('%', #{_cd_nm}, '%')
    AND cd_no LIKE CONCAT('%', #{_cd_no}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')


LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_CD


    AND cd_id = #{cd_id}
    AND grp_id = #{grp_id}
    AND cd_nm = #{cd_nm}
    AND cd_no = #{cd_no}
    AND use_yn = #{use_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND cd_id LIKE CONCAT('%', #{_cd_id}, '%')
    AND grp_id LIKE CONCAT('%', #{_grp_id}, '%')
    AND cd_nm LIKE CONCAT('%', #{_cd_nm}, '%')
    AND cd_no LIKE CONCAT('%', #{_cd_no}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

SELECT

    cd_id,
    grp_id,
    cd_nm,
    cd_no,
    use_yn,
    sys_crt_dt,
    sys_crt_usr_id,
    sys_upd_dt,
    sys_upd_usr_id
FROM SYS_CD


    AND cd_id = #{cd_id}
    AND grp_id = #{grp_id}
    AND cd_nm = #{cd_nm}
    AND cd_no = #{cd_no}
    AND use_yn = #{use_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND cd_id LIKE CONCAT('%', #{_cd_id}, '%')
    AND grp_id LIKE CONCAT('%', #{_grp_id}, '%')
    AND cd_nm LIKE CONCAT('%', #{_cd_nm}, '%')
    AND cd_no LIKE CONCAT('%', #{_cd_no}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

mapper.insert:
INSERT INTO SYS_CD(

     cd_id 
    , grp_id 

    , cd_nm
    , cd_no
    , use_yn
    , sys_crt_dt
    , sys_crt_usr_id
    , sys_upd_dt
    , sys_upd_usr_id
)
VALUES(

     #{cd_id}
    , #{grp_id}

    , #{cd_nm}
    , #{cd_no}
    , #{use_yn}
    , #{sys_crt_dt}
    , #{sys_crt_usr_id}
    , #{sys_upd_dt}
    , #{sys_upd_usr_id}
)

