mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY system_create_date DESC )AS no,

        top_office_code, 
        office_name, 
        office_code, 
        office_type, 
        office_type_code, 
        system_create_userid, 
        system_create_date, 
        system_update_userid, 
        system_update_date 
    FROM SYS_OFFICE


        AND top_office_code = #{top_office_code}
        AND office_name = #{office_name}
        AND office_code = #{office_code}
        AND office_type = #{office_type}
        AND office_type_code = #{office_type_code}
        AND system_create_userid = #{system_create_userid}
        AND system_create_date = #{system_create_date}
        AND system_update_userid = #{system_update_userid}
        AND system_update_date = #{system_update_date}

        AND top_office_code LIKE CONCAT('%', #{_top_office_code}, '%')
        AND office_name LIKE CONCAT('%', #{_office_name}, '%')
        AND office_code LIKE CONCAT('%', #{_office_code}, '%')
        AND office_type LIKE CONCAT('%', #{_office_type}, '%')
        AND office_type_code LIKE CONCAT('%', #{_office_type_code}, '%')
        AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
        AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
        AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
        AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_OFFICE


    AND top_office_code = #{top_office_code}
    AND office_name = #{office_name}
    AND office_code = #{office_code}
    AND office_type = #{office_type}
    AND office_type_code = #{office_type_code}
    AND system_create_userid = #{system_create_userid}
    AND system_create_date = #{system_create_date}
    AND system_update_userid = #{system_update_userid}
    AND system_update_date = #{system_update_date}

    AND top_office_code LIKE CONCAT('%', #{_top_office_code}, '%')
    AND office_name LIKE CONCAT('%', #{_office_name}, '%')
    AND office_code LIKE CONCAT('%', #{_office_code}, '%')
    AND office_type LIKE CONCAT('%', #{_office_type}, '%')
    AND office_type_code LIKE CONCAT('%', #{_office_type_code}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')

SELECT

    top_office_code,
    office_name,
    office_code,
    office_type,
    office_type_code,
    system_create_userid,
    system_create_date,
    system_update_userid,
    system_update_date
FROM SYS_OFFICE


    AND top_office_code = #{top_office_code}
    AND office_name = #{office_name}
    AND office_code = #{office_code}
    AND office_type = #{office_type}
    AND office_type_code = #{office_type_code}
    AND system_create_userid = #{system_create_userid}
    AND system_create_date = #{system_create_date}
    AND system_update_userid = #{system_update_userid}
    AND system_update_date = #{system_update_date}

    AND top_office_code LIKE CONCAT('%', #{_top_office_code}, '%')
    AND office_name LIKE CONCAT('%', #{_office_name}, '%')
    AND office_code LIKE CONCAT('%', #{_office_code}, '%')
    AND office_type LIKE CONCAT('%', #{_office_type}, '%')
    AND office_type_code LIKE CONCAT('%', #{_office_type_code}, '%')
    AND system_create_userid LIKE CONCAT('%', #{_system_create_userid}, '%')
    AND system_create_date LIKE CONCAT('%', #{_system_create_date}, '%')
    AND system_update_userid LIKE CONCAT('%', #{_system_update_userid}, '%')
    AND system_update_date LIKE CONCAT('%', #{_system_update_date}, '%')

