mapper.select:
SELECT * FROM (
    SELECT
        row_number() over (ORDER BY sys_crt_dt DESC )AS no,

        cfg_grp_id, 
        cfg_grp_nm, 
        use_yn, 
        sys_crt_dt, 
        sys_crt_usr_id, 
        sys_upd_dt, 
        sys_upd_usr_id 
    FROM SYS_SITE_CFG_GRP


        AND cfg_grp_id = #{cfg_grp_id}
        AND cfg_grp_nm = #{cfg_grp_nm}
        AND use_yn = #{use_yn}
        AND sys_crt_dt = #{sys_crt_dt}
        AND sys_crt_usr_id = #{sys_crt_usr_id}
        AND sys_upd_dt = #{sys_upd_dt}
        AND sys_upd_usr_id = #{sys_upd_usr_id}

        AND cfg_grp_id LIKE CONCAT('%', #{_cfg_grp_id}, '%')
        AND cfg_grp_nm LIKE CONCAT('%', #{_cfg_grp_nm}, '%')
        AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
        AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
        AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
        AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
        AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

) AS sub

LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_SITE_CFG_GRP


    AND cfg_grp_id = #{cfg_grp_id}
    AND cfg_grp_nm = #{cfg_grp_nm}
    AND use_yn = #{use_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND cfg_grp_id LIKE CONCAT('%', #{_cfg_grp_id}, '%')
    AND cfg_grp_nm LIKE CONCAT('%', #{_cfg_grp_nm}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

SELECT

    cfg_grp_id,
    cfg_grp_nm,
    use_yn,
    sys_crt_dt,
    sys_crt_usr_id,
    sys_upd_dt,
    sys_upd_usr_id
FROM SYS_SITE_CFG_GRP


    AND cfg_grp_id = #{cfg_grp_id}
    AND cfg_grp_nm = #{cfg_grp_nm}
    AND use_yn = #{use_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}

    AND cfg_grp_id LIKE CONCAT('%', #{_cfg_grp_id}, '%')
    AND cfg_grp_nm LIKE CONCAT('%', #{_cfg_grp_nm}, '%')
    AND use_yn LIKE CONCAT('%', #{_use_yn}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')

mapper.insert:
INSERT INTO SYS_SITE_CFG_GRP(

     cfg_grp_id 
    , cfg_grp_nm 
    , use_yn 
    , sys_crt_dt 
    , sys_crt_usr_id 

    , sys_upd_dt
    , sys_upd_usr_id
)
VALUES(

     #{cfg_grp_id}
    , #{cfg_grp_nm}
    , #{use_yn}
    , #{sys_crt_dt}
    , #{sys_crt_usr_id}

    , #{sys_upd_dt}
    , #{sys_upd_usr_id}
)

mapper.delete:
DELETE FROM SYS_SITE_CFG_GRP


    AND cfg_grp_id = #{cfg_grp_id}

    AND cfg_grp_nm = #{cfg_grp_nm}
    AND use_yn = #{use_yn}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}
