mapper.select:
SELECT
    row_number() over (ORDER BY sys_crt_dt DESC )AS no,
    id, 
    email, 
    pwd, 
    role, 
    user_id, 
    offc_cd,
    (SELECT offc_nm FROM SYS_OFFC WHERE offc_cd = SYS_USER.offc_cd) AS offc_nm,
    sys_crt_usr_id, 
    sys_crt_dt, 
    sys_upd_usr_id, 
    sys_upd_dt, 
    user_nm, 
    tel, 
    addr, 
    addr_dtl, 
    post_cd, 
    uuid 
FROM SYS_USER


    AND id = #{id}
    AND email = #{email}
    AND pwd = #{pwd}
    AND role = #{role}
    AND user_id = #{user_id}
    AND offc_cd = #{offc_cd}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND user_nm = #{user_nm}
    AND tel = #{tel}
    AND addr = #{addr}
    AND addr_dtl = #{addr_dtl}
    AND post_cd = #{post_cd}
    AND uuid = #{uuid}

    AND id LIKE CONCAT('%', #{_id}, '%')
    AND email LIKE CONCAT('%', #{_email}, '%')
    AND pwd LIKE CONCAT('%', #{_pwd}, '%')
    AND role LIKE CONCAT('%', #{_role}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND offc_cd LIKE CONCAT('%', #{_offc_cd}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND user_nm LIKE CONCAT('%', #{_user_nm}, '%')
    AND tel LIKE CONCAT('%', #{_tel}, '%')
    AND addr LIKE CONCAT('%', #{_addr}, '%')
    AND addr_dtl LIKE CONCAT('%', #{_addr_dtl}, '%')
    AND post_cd LIKE CONCAT('%', #{_post_cd}, '%')
    AND uuid LIKE CONCAT('%', #{_uuid}, '%')


LIMIT #{row_range} OFFSET #{offset}

SELECT CEIL(COUNT(*) / #{row_range}) FROM SYS_USER


    AND id = #{id}
    AND email = #{email}
    AND pwd = #{pwd}
    AND role = #{role}
    AND user_id = #{user_id}
    AND offc_cd = #{offc_cd}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND user_nm = #{user_nm}
    AND tel = #{tel}
    AND addr = #{addr}
    AND addr_dtl = #{addr_dtl}
    AND post_cd = #{post_cd}
    AND uuid = #{uuid}

    AND id LIKE CONCAT('%', #{_id}, '%')
    AND email LIKE CONCAT('%', #{_email}, '%')
    AND password LIKE CONCAT('%', #{_pwd}, '%')
    AND role LIKE CONCAT('%', #{_role}, '%')
    AND user_id LIKE CONCAT('%', #{_user_id}, '%')
    AND offc_cd LIKE CONCAT('%', #{_offc_cd}, '%')
    AND sys_crt_usr_id LIKE CONCAT('%', #{_sys_crt_usr_id}, '%')
    AND sys_crt_dt LIKE CONCAT('%', #{_sys_crt_dt}, '%')
    AND sys_upd_usr_id LIKE CONCAT('%', #{_sys_upd_usr_id}, '%')
    AND sys_upd_dt LIKE CONCAT('%', #{_sys_upd_dt}, '%')
    AND user_nm LIKE CONCAT('%', #{_user_nm}, '%')
    AND tel LIKE CONCAT('%', #{_tel}, '%')
    AND address LIKE CONCAT('%', #{_addr}, '%')
    AND addr_dtl LIKE CONCAT('%', #{_addr_dtl}, '%')
    AND post_cd LIKE CONCAT('%', #{_post_cd}, '%')
    AND uuid LIKE CONCAT('%', #{_uuid}, '%')

SELECT

    id,
    email,
    pwd,
    role,
    user_id,
    offc_cd,
    (SELECT offc_nm FROM SYS_OFFC WHERE offc_cd = SYS_USER.offc_cd) AS offc_nm,
    sys_crt_usr_id,
