    AND id = #{id}

    AND email = #{email}
    AND pwd = #{pwd}
    AND role = #{role}
    AND user_id = #{user_id}
    AND offc_cd = #{offc_cd}
    AND sys_crt_usr_id = #{sys_crt_usr_id}
    AND sys_crt_dt = #{sys_crt_dt}
    AND sys_upd_usr_id = #{sys_upd_usr_id}
    AND sys_upd_dt = #{sys_upd_dt}
    AND user_nm = #{user_nm}
    AND tel = #{tel}
    AND addr = #{addr}
    AND addr_dtl = #{addr_dtl}
    AND post_cd = #{post_cd}
    AND uuid = #{uuid}

mapper.insert:
INSERT INTO SYS_USER (
    id
        , email
        , pwd
        , role
        , user_id
        , offc_cd
        , sys_crt_usr_id
        , sys_crt_dt
        , user_nm
        , tel
        , addr
        , addr_dtl
        , post_cd
        , uuid
) VALUES (
    #{id}
        , #{email}
        , #{pwd}
        , #{role}
        , #{user_id}
        , #{offc_cd}
        , #{sys_crt_usr_id}
        , #{sys_crt_dt}
        , #{user_nm}
        , #{tel}
        , #{addr}
        , #{addr_dtl}
        , #{post_cd}
        , #{uuid}
)
